const initialState = {
  books: [
    {
      id: 1,
      name: 'Production-Ready Microservices',
      author: 'Susan J. Flower'
    },
    {
      id: 2,
      name: 'Release It!',
      author: 'Michal T. Nygard'
    }
  ]
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'BOOKS_LOADED':
      return {
        books: action.payload
      };

    default:
      return state;
  }
};

export default reducer;
